import React from 'react';
import ModernDatepicker from 'react-modern-datepicker';
import { MDBBtn, MDBModalBody, MDBModal, MDBModalHeader, MDBIcon } from 'mdbreact';

export default class Datepicker extends React.Component {
  state = {
    modal: false
  }

  constructor(props) {
    super(props);
    this.state = {
        startDate: new Date(), // can be any of these ['dayjs()', '', null, new Date(2018,12,1)]
    };
    this.handleChange = this.handleChange.bind(this);
  }

  toggle = () => {
    this.setState({
      modal: !this.state.modal
    });
  }

  handleChange(date) {
    this.setState({
      startDate: date,
    });
  }

  render() {
    return (
      <div>
        <MDBBtn flat className="z-depth-0" onClick={this.toggle}><MDBIcon size="lg" far icon="calendar-alt" /></MDBBtn>
        <MDBModal isOpen={this.state.modal} toggle={this.toggle}>
          <MDBModalHeader toggle={this.toggle}>MDBModal title</MDBModalHeader>
          <MDBModalBody className="colorConviasa">
            <ModernDatepicker
              date={this.state.startDate}
              format={'DD-MM-YYYY'}
              showBorder
              onChange={date => this.handleChange(date)}
              placeholder={'Seleccione una fecha'}
              primaryColor={'#717171'}
              secondaryColor={'#EE7500'}
              primaryTextColor={'white'}
              secondaryTextColor={'#acd0c0'}

            />
          </MDBModalBody>
        </MDBModal>
      </div>
    );
  }
}
